mapper.select:
select *
from book
where (b_status='Y' or b_status= 'R' or b_status= 'W') and b_no = #{bNo}

select count(*)
from book
where b_status='Y' and b_isbn = #{bISBN}

select count(*)
from book
where b_isbn= #{bISBN}

mapper.update:
update book
set b_status = 'W'
where b_no = #{bookNo}

mapper.select:
select count(*)
from book_reservation
where USER_ID = #{userId}

mapper.update:
update BOOK_REQUEST
set bq_status = 'Y'
where bq_no = #{bq_no}

update BOOK_REQUEST
set bq_status = 'N'
where bq_no = #{bq_no}

update BOOK_REQUEST
set bq_status = 'C'
where bq_no = #{bq_no}

update BOOK_RESERVATION
set bv_status = 'C'
where bv_no = #{bv_no}

update BOOK
set b_status = 'Y'
where b_no = #{b_no}

update BOOK_RESERVATION
set bv_status = 'Y' 
where bv_no = #{bv_no}

update BOOK
set b_status = 'R'
where b_no = #{b_no}

update book_rent
set br_return_date = to_char((sysdate)+3,'YYYY/MM/DD HH24:MI'), br_status = 'E'
where br_no = #{br_no}

update book_rent
set br_status = 'Y'
where br_no = #{br_no}

update book
set b_status = 'Y'
where b_no = #{b_no}

update book
set B_STATUS = 'N'
where B_NO = #{bNo}
