mapper.update:
UPDATE STUDYSEAT
SET SS_USE = 'R' , CERT_CODE = #{cert_code} , STUDENT_ID = #{student_id}
WHERE SS_NO = #{ss_no}

  		UPDATE STUDYSEAT
		SET SS_USE = 'N' , CERT_CODE = NULL , STUDENT_ID = NULL
		WHERE SS_NO = #{cancelId}

  		UPDATE STUDYSEAT 
		SET SS_USE = 'Y' , SS_CERT = 'Y'
		WHERE STUDENT_ID = #{student_id} AND CERT_CODE = #{cert_code}

UPDATE SEAT_HISTORY
SET SH_STATUS = 'U'
WHERE STUDENT_ID = #{student_id} AND SS_NO = #{ss_no}

UPDATE STUDYSEAT
SET SS_USE = 'N' , SS_CERT = 'N' , CERT_CODE = NULL , STUDENT_ID = NULL , START_TIME = NULL , END_TIME = NULL
WHERE SS_NO = #{ss_no} AND STUDENT_ID = #{student_id}

  		UPDATE SEAT_HISTORY 
		SET SH_STATUS = 'E'
		WHERE SS_NO = #{ss_no} AND STUDENT_ID = #{student_id}
